    private void jnsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jnsActionPerformed
        switch(jns.getSelectedIndex()){
            case 1 -> {
                hrg.setText(String.valueOf("10000"));
            }
            case 2 -> {
                hrg.setText(String.valueOf("20000"));
            }
            case 3 -> {
            hrg.setText(String.valueOf("20000"));
            }
            case 4 -> {
            hrg.setText(String.valueOf("30000"));
            }
            
    }
    }//GEN-LAST:event_jnsActionPerformed

