    private void selesaiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_selesaiActionPerformed
          int JumlahBarang=Integer.parseInt(psnn.getText());
          int HargaBarang=Integer.parseInt(hrg.getText());
          ttl.setText(Integer.toString(HargaBarang*JumlahBarang));
          kp.setText(String.valueOf(jns));
          tm.setText(String.valueOf(psnn));
    }//GEN-LAST:event_selesaiActionPerformed

    private void hapusActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_hapusActionPerformed
        psnn.setText("");
        kp.setText("");
        hrg.setText("");
        tm.setText("");
        ttl.setText("");
        jns.setSelectedIndex(0);
    }//GEN-LAST:event_hapusActionPerformed

